import { useState } from "react";
import { Task } from "../utils/interface";

const TaskForm: React.FC<{ onSubmit: (task: Task) => void }> = ({ onSubmit }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [status, setStatus] = useState<'To Do' | 'In Progress' | 'Done'>('To Do');

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        onSubmit({ id: Math.floor(Math.random() * 1000), title, description, status });
        setTitle('');
        setDescription('');
        setStatus('To Do');
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>Title:</label>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
            <label>Description:</label>
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
            <label>Status:</label>
            <select value={status} onChange={(e) => setStatus(e.target.value as 'To Do' | 'In Progress' | 'Done')}>
                <option value="To Do">To Do</option>
                <option value="In Progress">In Progress</option>
                <option value="Done">Done</option>
            </select>
            <button type="submit">Add Task</button>
        </form>
    );
};

export default TaskForm;
