import { useState } from "react";
import { Task } from "../utils/interface";

const TaskForm: React.FC<{ onSubmit: (task: Task) => void }> = ({ onSubmit }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [status, setStatus] = useState<'To Do' | 'In Progress' | 'Done'>('To Do');

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        onSubmit({ id: Math.floor(Math.random() * 1000), title, description, status });
        setTitle('');
        setDescription('');
        setStatus('To Do');
    };

    return (
        <form onSubmit={handleSubmit} className="p-4 border rounded-lg bg-gray-800 text-white">
            <label className="block mb-2 text-sm">Title:</label>
            <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                className="w-full py-2 px-3 mb-4 bg-gray-700 text-white rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
            />
            <label className="block mb-2 text-sm">Description:</label>
            <textarea
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="w-full py-2 px-3 mb-4 bg-gray-700 text-white rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
            />
            <label className="block mb-2 text-sm">Status:</label>
            <select
                value={status}
                onChange={(e) => setStatus(e.target.value as 'To Do' | 'In Progress' | 'Done')}
                className="w-full py-2 px-3 mb-4 bg-gray-700 text-white rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
                <option value="To Do">To Do</option>
                <option value="In Progress">In Progress</option>
                <option value="Done">Done</option>
            </select>
            <button
                type="submit"
                className="w-full py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
                Add Task
            </button>
        </form>

    );
};

export default TaskForm;
