import React, { useState } from 'react';
import { Task } from '../utils/interface';
import TaskForm from './TaskForm';
import TaskList from './TaskList';
import SidebarItem from './common/sidebar';


const TaskManager: React.FC = () => {
    const [tasks, setTasks] = useState<Task[]>([]);
    const [filter, setFilter] = useState<'all' | 'To Do' | 'In Progress' | 'Done'>('all');
    const [showTaskForm, setShowTaskForm] = useState<boolean>(false);
    const [sidebarOpen, setSidebarOpen] = useState<boolean>(false);

    const handleAddTask = (newTask: Task) => {
        setTasks([...tasks, newTask]);
        setShowTaskForm(false);
    };

    const handleUpdateStatus = (taskId: number) => {
        const updatedTasks = tasks.map((task) =>
            task.id === taskId ? { ...task, status: task.status === 'To Do' ? 'In Progress' : 'Done' } : task
        );
        setTasks(updatedTasks as any);
    };

    const handleDeleteTask = (taskId: number) => {
        const updatedTasks = tasks.filter((task) => task.id !== taskId);
        setTasks(updatedTasks);
    };

    const filteredTasks = filter === 'all' ? tasks : tasks.filter((task) => task.status === filter);

    return (
        <div className="flex flex-col md:flex-row h-screen bg-gray-900 text-white">
            {/* Sidebar */}
            <div className={`md:w-1/5 bg-gray-800 ${sidebarOpen ? 'block' : 'hidden'} md:block`}>
                <div className="p-4">
                    <SidebarItem onClick={() => setShowTaskForm(true)}>Create Task</SidebarItem>
                </div>
                <div className="p-4">
                    <SidebarItem onClick={() => setFilter('all')}>All Tasks</SidebarItem>
                    <SidebarItem onClick={() => setFilter('To Do')}>To Do</SidebarItem>
                    <SidebarItem onClick={() => setFilter('In Progress')}>In Progress</SidebarItem>
                    <SidebarItem onClick={() => setFilter('Done')}>Done</SidebarItem>
                </div>
            </div>
            {/* Content */}
            <div className="md:w-4/5 p-4 bg-white overflow-y-scroll">
                {/* Navbar */}
                <nav className="flex justify-between items-center bg-gray-800 p-4">
                    <button
                        className="text-gray-200 focus:outline-none md:hidden"
                        onClick={() => setSidebarOpen(!sidebarOpen)}
                    >
                        {sidebarOpen ? (
                            <svg
                                xmlns="http://www.w3.org/2000/svg"
                                className="h-6 w-6"
                                fill="none"
                                viewBox="0 0 24 24"
                                stroke="currentColor"
                            >
                                <path
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                    strokeWidth={2}
                                    d="M6 18L18 6M6 6l12 12"
                                />
                            </svg>
                        ) : (
                            <svg
                                xmlns="http://www.w3.org/2000/svg"
                                className="h-6 w-6"
                                fill="none"
                                viewBox="0 0 24 24"
                                stroke="currentColor"
                            >
                                <path
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                    strokeWidth={2}
                                    d="M4 6h16M4 12h16m-7 6h7"
                                />
                            </svg>
                        )}
                    </button>
                    <h1 className="text-lg font-bold text-white">Task Management</h1>
                </nav>
                {/* Main Content */}
                {showTaskForm ? (
                    <TaskForm onSubmit={handleAddTask} />
                ) : (
                    <TaskList tasks={filteredTasks} onUpdateStatus={handleUpdateStatus} onDeleteTask={handleDeleteTask} />
                )}
            </div>
        </div>
    );
};

export default TaskManager;
