import { Task } from "../utils/interface";

const TaskList: React.FC<{
    tasks: Task[];
    onUpdateStatus: (id: number) => void;
    onDeleteTask: (id: number) => void;
}> = ({ tasks, onUpdateStatus, onDeleteTask }) => {
    return (
        <ul className="mt-4 grid gap-4">
            {tasks.map((task) => (
                <li
                    key={task.id}
                    className="border p-4 rounded-lg bg-gray-800 text-white flex flex-col justify-between"
                >
                    <div>
                        <strong>{task.title}</strong>
                        <p>{task.description}</p>
                        <p>Status: {task.status}</p>
                    </div>
                    <div className="flex justify-between mt-2">
                        <button
                            onClick={() => onUpdateStatus(task.id)}
                            className="bg-blue-500 text-white rounded-lg px-3 py-1 hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                        >
                            Update Status
                        </button>
                        <button
                            onClick={() => onDeleteTask(task.id)}
                            className="bg-red-500 text-white rounded-lg px-3 py-1 hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-500"
                        >
                            Delete Task
                        </button>
                    </div>
                </li>
            ))}
        </ul>
    );
};

export default TaskList
